                    String isi = "";
                    if (position < 9) {
                        isi = itemValue.substring(3, itemValue.length());
                    } else if(position < 99) {
                        isi = itemValue.substring(4, itemValue.length());
                    }
                    else{
                        isi = itemValue.substring(5, itemValue.length());
                    }
                    isi = isi.replace("'", "");
                    ClipboardManager clipboard = (ClipboardManager) getSystemService(Context.CLIPBOARD_SERVICE);
